function loadArticle(){var a=$(".article");$.ajax({url:"../news.json",success:function(i){for(var r=n;r<n+4;r++)if(r<i.length){var t=$("<li></li>");t.html("<a href='javascript:;'><img src='../imgs/news/"+i[r].img+".jpg'><h4>"+i[r].title+"</h4></a><p>"+i[r].content+"</p><span>"+i[r].time+"</span>"),a.append(t)}},error:function(){var n=$("<p></p>");n.text("加载失败"),a.append(n)}})}var n=0;loadArticle(),$(".more").click(function(){n+=4,loadArticle()});